import React from 'react';

class StackTrace extends React.Component {
    constructor(props) {
        super(props);
        this.state = {traceClass: 'stackTraceShow'};
    }

    handleVisibility()
    {
        if (this.props.show) {
            this.setState({
                traceClass: 'stackTraceShow'
            })
        }
        else {
           this.setState({
                traceClass: 'stackTraceHide'
           })
        }
    }

    render() {
        //this.handleVisibility();
        if (!this.props.show)
        {
            alert("not showing stack trace");
            return (<div/>);
        }

        return (
            <div className={this.state.traceClass}>
                {this.props.trace}
            </div>
        );
    }
}

export default StackTrace;
