import React from 'react';

class InfoToggle extends React.Component {
    constructor(props) {
        super(props);
        this.state = { infoClass: 'infoHide'};

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        if (this.state.traceClass === 'stackTraceHide') {
            this.setState({
                infoClass: 'infoShow'
            })
        }
        else {
           this.setState({
                infoClass: 'infoHide'
           })
        }

        this.props.updateElement(this.state.infoClass);
    }

    render() {
        return (
            <a onClick={this.handleClick} className='info-button'/>
        );
    }
}

export default InfoToggle;
