import React from 'react';
import ThemeContext from './ThemeContext';
import Toolbar from './Toolbar'
import Log from './Log'
import ThemeSwitch from './ThemeSwitch'
import './App.css';

const ActiveCategories = React.createContext('activeCategories');

class App extends React.Component {
  constructor(props) {
    super(props);

    let startupTheme = 'dark';
    if ('theme' in localStorage) {
      startupTheme = localStorage.getItem('theme');
    }

    this.state = 
    {
      logs: [],
      categories: {
          'assert': true,        
          'error': true,
          'exception': true,
          'warning': true,
          'system': true,        
          'performance': true,
          'log': true, 
          'graphics': true, 
          'ai': true,
          'audio': true, 
          'content': true, 
          'logic': true, 
          'gui': true, 
          'input': true,  
          'network': true, 
          'animation': true, 
          'physics': true, 
          'event': true
      },
      theme: startupTheme
    }
    this.addLogData = this.addLogData.bind(this);
    this.toggleCategory = this.toggleCategory.bind(this);
    this.toggleTheme = this.toggleTheme.bind(this);
  }

  toggleTheme() {
    this.setState(state => ({
      logs: this.state.logs,
      categories: this.state.categories,
      theme:
        state.theme === 'dark'//themes.dark
          ? 'light'//themes.light
          : 'dark'//themes.dark,
    }));

    let body = document.getElementsByTagName("BODY")[0];
    if (this.state.theme !== 'dark')
    {
      localStorage.setItem('theme', 'dark');
      body.style.background = '#282c34';
    }
    else if (this.state.theme !== 'light')
    {
      localStorage.setItem('theme', 'light');
      body.style.background = 'rgb(235, 221, 193)';
      body.style.scrollbarFaceColor = ''
    }
  };

  addLogData(logArray)
  {
    this.setState({logs: logArray, categories: this.state.categories, theme: this.state.theme});
  }

  toggleCategory(key, isActive)
  {
    const categoriesCopy = Object.assign({}, this.state.categories);
    categoriesCopy[key] = isActive;
    this.setState({logs: this.state.logs, categories: categoriesCopy, theme: this.state.theme});
  }

  render() {
    const logs = this.state.logs.map((data, index) =>
      <Log key={index} filter={this.state.categories} timeStamp={data.TimeStamp} categories={data.Categories} log={data.Log} trace={data.StackTrace} info={'data.info'} />
    );

    return (
      <ThemeContext.Provider value={this.state.theme}>
        <div className="App">
          <Toolbar onLoadedLog={this.addLogData} onCategoryToggle={this.toggleCategory}/>
          <div className='logContainer'>
            {logs}
          </div>
          <ThemeSwitch swapTheme={this.toggleTheme}/>
        </div>
      </ThemeContext.Provider>
    );
  }
}

export default App;
