import React from 'react';

class StackTraceToggle extends React.Component {
    constructor(props) {
        super(props);
        this.state = { traceClass: 'stackTraceHide'};

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        if (this.state.traceClass === 'stackTraceHide') {
            this.setState({
                traceClass: 'stackTraceShow'
            })
        }
        else {
           this.setState({
                traceClass: 'stackTraceHide'
           })
        }

        this.props.updateElement(this.state.traceClass);
    }

    render() {
        return (
            <a onClick={this.handleClick} className='stackTrace-button'/>
        );
    }
}

export default StackTraceToggle;
