import React from 'react';
import Toolbar from './Toolbar'
import Log from './Log'
import './App.css';

const ActiveCategories = React.createContext('activeCategories');

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = 
    {
      categories: {
          'assert': true,        
          'error': true,
          'exception': true,
          'warning': true,
          'system': true,        
          'performance': true,
          'log': true, 
          'graphics': true, 
          'ai': true,
          'audio': true, 
          'content': true, 
          'logic': true, 
          'gui': true, 
          'input': true,  
          'network': true, 
          'animation': true, 
          'physics': true, 
          'event': true
      }
    }
    this.toggleCategory = this.toggleCategory.bind(this);
  }

  toggleCategory(key, isActive)
  {
    const categoriesCopy = Object.assign({}, this.state.categories);
    categoriesCopy[key] = isActive;
    this.setState({categories: categoriesCopy});
  }

  render() {
    return (
      <div className="App">
        <Toolbar onCategoryToggle={this.toggleCategory}/>
        <div>
          <Log filter={this.state.categories} timeStamp="now" categories={["log", "system"]} log="hello world" trace="stack" info="info" />
          <Log filter={this.state.categories} timeStamp="now" categories={["log", "system"]} log="hello world" trace="stack" info="info" />
          <Log filter={this.state.categories} timeStamp="now" categories={["log", "system"]} log="hello world" trace="stack" info="info" />
          <Log filter={this.state.categories} timeStamp="now" categories={["log", "system"]} log="hello world" trace="stack" info="info" />
          <Log filter={this.state.categories} timeStamp="now" categories={["log", "system"]} log="hello world" trace="stack" info="info" />
        </div>
      </div>
    );
  }
}

export default App;
