import React from 'react';
import Toolbar from './Toolbar'
import Log from './Log'
import './App.css';

const ActiveCategories = React.createContext('activeCategories');

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = 
    {
      logs: [],
      categories: {
          'assert': true,        
          'error': true,
          'exception': true,
          'warning': true,
          'system': true,        
          'performance': true,
          'log': true, 
          'graphics': true, 
          'ai': true,
          'audio': true, 
          'content': true, 
          'logic': true, 
          'gui': true, 
          'input': true,  
          'network': true, 
          'animation': true, 
          'physics': true, 
          'event': true
      }
    }
    this.addLogData = this.addLogData.bind(this);
    this.toggleCategory = this.toggleCategory.bind(this);
  }

  addLogData(logArray)
  {
    this.setState({logs: logArray, categories: this.state.categories});
  }

  toggleCategory(key, isActive)
  {
    const categoriesCopy = Object.assign({}, this.state.categories);
    categoriesCopy[key] = isActive;
    this.setState({logs: this.state.logs, categories: categoriesCopy});
  }

  render() {
    const logs = this.state.logs.map((data, index) =>
      <Log key={index} filter={this.state.categories} timeStamp={data.TimeStamp} categories={data.Categories} log={data.Log} trace={data.StackTrace} info={'data.info'} />
    );

    return (
      <div className="App">
        <Toolbar onLoadedLog={this.addLogData} onCategoryToggle={this.toggleCategory}/>
        <div className='logContainer'>
          {logs}
        </div>
      </div>
    );
  }
}

export default App;
