import React from 'react';

class Log extends React.Component {
    constructor(props) {
        super(props);
        this.state = { traceClass: 'stackTraceHide', infoClass: 'infoHide' };

        this.handleClick = this.handleStackTraceClick.bind(this);
        //this.handleClick = this.handleInfoClick.bind(this);
    }

    handleStackTraceClick() {
        if (this.state.traceClass === 'stackTraceHide') {
            this.setState({
                traceClass: 'stackTraceShow',
                infoClass: this.state.infoClass
            })
        }
        else {
           this.setState({
                traceClass: 'stackTraceHide',
                infoClass: this.state.infoClass
           })
        }
    }

    handleInfoClick() {
        if (this.state.traceClass === 'stackTraceHide') {
            this.setState({
                traceClass: this.state.traceClass,
                infoClass: 'infoClassShow'
            })
        }
        else {
           this.setState({
                traceClass: this.state.traceClass,
                infoClass: 'infoClassHide'
           })
        }
    }

    render() {
        return (
            <div id={this.props.category} className='log'>
                <table className='logContents'>
                    <tbody>
                        <tr>
                            <td width="10%">{this.props.timeStamp}</td>
                            <td width="85%">{this.props.log}</td>
                            <td width="2.5%">
                                <a onClick={this.handleClick} className='stackTrace-button' style={{textAlign: "right"}}/>
                            </td>
                            <td width="2.5%">
                                <a onClick={this.handleClick} className='info-button' style={{textAlign: "left"}}/>
                            </td>
                        </tr>
                    </tbody>
                </table>
                <div className={this.state.traceClass}>
                    {this.props.stackTrace}
                </div>
                <div className={this.state.infoClass}>
                    {this.props.info}
                </div>
            </div>
        );
    }
}

