import React from 'react';

class FileUploader extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    readSingleFile = (e) => {
        var file = e.target.files[0];
        if (!file) {
            return;
        }
        var reader = new FileReader();
        reader.onload = (e) => {
            let contents = e.target.result;
            this.props.onLoadedLog(JSON.parse(contents));
        };
        reader.readAsText(file);
    }

    readDroppedFile = (e) => {
        console.log('File(s) dropped');
        e.preventDefault();
    }
    
    render() {
        return (
            <div class='fileButtonContainer' onDrop={this.readDroppedFile}>
                <input type='file' id='hiddenUpload' className='categoryButton' onChange={this.readSingleFile} />
                <a onClick={() => document.getElementById('upload').click()} id='upload' className='categoryButton'>upload</a>
            </div>
        );
    }
}

export default FileUploader;
