import React from 'react';

class CategoryButton extends React.Component {
    constructor(props) {
        super(props);
        this.state = { traceClass: 'stackTraceHide', infoClass: 'infoHide' };

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        if (this.state.traceClass === 'stackTraceHide') {
            this.setState({
                traceClass: 'stackTraceShow',
                infoClass: this.state.infoClass
            })
        }
        else {
           this.setState({
                traceClass: 'stackTraceHide',
                infoClass: this.state.infoClass
           })
        }
    }

    render() {
        return (
            <a className='categoryButton'>category</a>
        );
    }
}

export default CategoryButton;
