import React from 'react';

class Log extends React.Component {
    constructor(props) {
        super(props);
        this.state = { traceClass: 'stackTraceHide' };

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        if (this.state.traceClass === 'stackTraceHide') {
            this.setState({
                traceClass: 'stackTraceShow'
            })
        }
        else {
           this.setState({
                traceClass: 'stackTraceHide',
           })
        }
        // this.setState(state => ({
        //     showStackTrace: !state.showStackTrace
        // }));
    }

    render() {
        return (
            <div className='log'>
                <table className='logContents'>
                    <tbody>
                        <tr>
                            <td width="10%">{this.props.timeStamp}</td>
                            <td width="85%">{this.props.log}</td>
                            <td width="2.5%">
                                <a onClick={this.handleClick} className='stackTrace-button'/>
                            </td>
                            <td width="2.5%">
                                <a onClick={this.handleClick} className='info-button'/>
                            </td>
                        </tr>
                    </tbody>
                </table>
                <div className={this.state.traceClass}>
                    {this.props.stackTrace}
                </div>
            </div>
        );
    }
}

