import React from 'react';
import StackTraceToggle from './StackTraceToggle';
import InfoToggle from './InfoToggle';
import StackTrace from './StackTrace';
import Info from './Info';

class Log extends React.Component {
    constructor(props) {
        super(props);
        this.state = { traceClass: 'stackTraceHide', infoClass: 'infoHide' };

        this.handleClick = this.handleStackTraceClick.bind(this);
        //this.handleClick = this.handleInfoClick.bind(this);
    }

    handleStackTraceClick() {
        if (this.state.traceClass === 'stackTraceHide') {
            this.setState({
                traceClass: 'stackTraceShow',
                infoClass: this.state.infoClass
            })
        }
        else {
           this.setState({
                traceClass: 'stackTraceHide',
                infoClass: this.state.infoClass
           })
        }
    }

    handleInfoClick() {
        if (this.state.traceClass === 'stackTraceHide') {
            this.setState({
                traceClass: this.state.traceClass,
                infoClass: 'infoClassShow'
            })
        }
        else {
           this.setState({
                traceClass: this.state.traceClass,
                infoClass: 'infoClassHide'
           })
        }
    }

    render() {
        const categoryTags = this.props.categories.map((data, index) =>
            <a id={data} className='categoryTag'>{data}</a>
        );

        const stackTrace = <StackTrace trace={this.props.trace}/>;
        const info = <Info info={this.props.info}/>;

        stackTrace.onToggle()

        return (
            <div className='log'>
                <table className='logContents'>
                    <tbody>
                        <tr>
                            <td width="5%">{this.props.timeStamp}</td>
                            <td width="10%">
                                {categoryTags}
                            </td>
                            <td width="80%">{this.props.log}</td>
                            <td width="2.5%">
                                <StackTraceToggle updateElement={() => stackTrace.onToggle()}/>
                            </td>
                            <td width="2.5%">
                                <InfoToggle updateElement={() => info.onToggle()}/>
                            </td>
                        </tr>
                    </tbody>
                </table>
                {stackTrace}
                {info}
            </div>
        );
    }
}

